import React from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { setCategoryId } from "../redux/slices/filterSlices.js";
import { SearchContext } from "../App.js";

import Categories from "../components/Categories";
import Sort from "../components/Sort";
import PizzaBlock from "../components/PizzaBlock.jsx";

function Home() {
  const dispatch = useDispatch();
  const categoryId= useSelector((state) => state.filter.cat);
  const sortType = useSelector((state) => state.filter)
  const { searchValue } = React.useContext(SearchContext);
  const [items, setItems] = React.useState([]);

  const onClickCategories = (id) => {
    dispatch(setCategoryId(id));
  };
  
    const sortBy = sort.sortProperty;
    const category = categoryId > 0 ? `category=${categoryId}` : '';
    const search = searchValue ? `&title=${searchValue}` : '';

  React.useEffect(() => {
    axios.get(`https://ba9d8a7e491cfaf0.mokky.dev/items?${category}&sortBy=${sortBy}${search}`)
         .then((res) => { 
            setItems(res.data)
        })
  }, [categoryId, sort.sortProperty, searchValue]);

  const pizzas = items.map((obj) => {
      return <PizzaBlock searchValue={searchValue} key={obj.id} {...obj} />;
  });

  return (
    <>
      <div className="content__top">
        <Categories value={categoryId} onClickCategory={(i) => onClickCategories(i)} />
        <Sort />
      </div>
      <h2 className="content__title">Пиццы</h2>
      <div className="content__items">{pizzas}</div>
    </>
  );
}

export default Home;
