import React from "react";

import Categories from "../components/Categories";
import Sort from "../components/Sort";
import PizzaBlock from "../components/PizzaBlock";
import Skeleton from "../components/Skeleton";

import { SearchContext } from "../App";

function Home() {


  const { searchValue } = React.useContext(SearchContext);
  const [items, setItems] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);

  // const onClickCategors = (id) => {
  //   dispatch(setCategoryId(id));
  // };

  console.log(sortType)

  //  alert(sortType.replace("-", ""));

  React.useEffect(() => {
    setIsLoading(true);

    // const sortBy = sortType.replace('-', '');
    // const s = sortType.replace('-', '');
    // const order = sortType.includes('-') ? 'asc' : 'desc';
    const category = categoryId > 0 ? `category=${categoryId}` : '';

    fetch(
      `https://ba9d8a7e491cfaf0.mokky.dev/items?${category}`
    )
      .then((res) => res.json())
      .then((arr) => {
        setItems(arr);
        setIsLoading(false);
      });
    window.scrollTo(0, 0);
  }, [categoryId, sortType]);

  const skeletons = [...new Array(3)].map((_, i) => <Skeleton key={i} />);
  const pizzas = items
    .filter((pizz) =>
      pizz.title.toLowerCase().includes(searchValue.toLowerCase())
    )
    .map((obj) => {
      return <PizzaBlock searchValue={searchValue} key={obj.id} {...obj} />;
    });

  return (
    <>
      <div className="content__top">
        <Categories
          value={categoryId}
          // onClickCategory={(i) => onClickCategors(i)}
        />
        <Sort />
      </div>
      <h2 className="content__title">Пиццы</h2>
      <div className="content__items">{isLoading ? skeletons : pizzas}</div>
    </>
  );
}
export default Home;
